(function() {
	
	var grTask = new GlideRecord('u_standard_change_task');
	
	grTask.addQuery('unique_id', current.getValue('unique_id'));  //Checks for match between change and task
	grTask.addEncodedQuery('u_active=true^u_change_type=standard');
	grTask.addQuery('u_order', '>', workflow.scratchpad.ordertask); //Order has to be > then previous task
	grTask.orderBy('u_order');
	grTask.setLimit(1);
	grTask.query();
	
	if(grTask.next()){
		workflow.scratchpad.ordertask = grTask.getValue('u_order');
		workflow.scratchpad.systask = grTask.getUniqueValue();
		
		
		return "yes";
		
	}
	else
		return "no";
	
	
	
} )();

