var grTask = new GlideRecord('u_standard_change_task');
grTask.get(workflow.scratchpad.systask);

var task = new GlideRecord('change_task');    //Create one task at a time 
task.newRecord();
task.change_request = current.getUniqueValue();


//Set values to change task table

task.short_description = grTask.getValue('u_short_description');
task.description = grTask.getValue('u_description');
task.assignment_group = grTask.getValue('u_assignment_group');
task.u_sla_duration = grTask.getValue('u_sla_duration');
task.change_task_type = grTask.getValue('u_task_type');
task.u_auto_close= grTask.getValue('u_auto_close');


var sys_id = task.insert();   //sys_id to get task in once
workflow.scratchpad.tasksysid = sys_id;   ///Put in Scratchpad to take to next activity

